function director () {
  var name , 
  quotes = [];

  this.setName = function (pName) {
  	name = pName;
  };

  this.getName = function () {
  	return name;
  };

  this.addQuote = function (pQuote) {
  	quotes.push(pQuote);
  };

  this.speak = function () {
  	var returnValue = getName() + ' says ' , i = 0;

  	for (i = 0 ; i < this.quotes.length ; i++) {
  		returnValue.append(quotes[i] + '\n');
  	}
  	return returnValue;
  };
}

module.exports = director;